import React, { useState } from "react";
import "../style.css";
import { FiSearch } from "react-icons/fi";
import axios from 'axios';

const SearchBar = () => {
    const [location, setLocation] = useState("");
    const [nursingHomes, setNursingHomes] = useState([]);

    const fetchNursingHomes = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/search?location=${encodeURIComponent(location)}`);
            setNursingHomes(response.data);

        } catch (error) {
            console.error("Error fetching nursing home: ", error);
        }
    };

    return (
        <div className="search-container">
            <h1 className="search-title">Find your own nearby convenient Nursing Home</h1>

            <div className="search_bar">
                <div className="row">
                    <input 
                        className="search_input" 
                        type="text" 
                        placeholder="Enter your location"
                        value={location}
                        onChange={(e) => setLocation(e.target.value)}
                    />
                    <button className="search_button" onClick={fetchNursingHomes}><FiSearch size={24}/></button>
                </div>
            </div>
            <ul>
                {nursingHomes.map((home, index) => (
                    <li key={index}>{home.name} - {home.vicinity}</li>
                ))}
            </ul>
        </div>
    );
}

export default SearchBar;
