import React, { useState, useEffect } from "react";
import "../style.css";
import { FiSearch } from "react-icons/fi";
import axios from 'axios';

const endpoint = 'http://localhost:8000/nursing-homes';
const SearchBar = () => {
    const [query, setQuery] = useState("");
    const [nursingHomes, setNursingHomes] = useState([]);
    const [filteredHomes, setFilteredHomes] = useState([]);
    const [showSuggestions, setShowSuggestions] = useState(false)

    useEffect(() => {
        fetch(endpoint)
            .then(response => response.json())
            .then(data => setNursingHomes(data));
    }, []);

    const handleSearch = () => {
        setShowSuggestions(false);
        const regex = new RegExp(query, 'gi');
        const matches = nursingHomes.filter(home => home.address_line_1.match(regex) || home.suburb.match(regex) || home.state.match(regex));
        setFilteredHomes(matches)

    }

    const handleInputChange =(e) => {
        const { value } = e.target;
        setQuery(value);
        setShowSuggestions(true);
        if(value.length > 0) {
            const regex = new RegExp(value, 'gi');
            const matches = nursingHomes.filter(home => home.address_line_1.match(regex) || home.suburb.match(regex) || home.state.match(regex));
            setFilteredHomes(matches)
        } else {
            setFilteredHomes([]);
        }
    }

    useEffect(() => {
        if(query.length > 0) {
            const regex = new RegExp(query, 'gi');
            const matches = nursingHomes.filter(home => home.address_line_1.match(regex) || home.suburb.match(regex) || home.state.match(regex));
            setFilteredHomes(matches)
        } else {
            setFilteredHomes([])
        }
    }, [query, nursingHomes]);


    return (
        <div className="search-container">
            <h1 className="search-title">Find your own nearby <br/>convenient Nursing Home</h1>

            <div className="search_bar">
                <div className="row">
                    <input 
                        className="search_input" 
                        type="text" 
                        placeholder="City or State"
                        value={query}
                        onChange={handleInputChange}
                    />
                    <button className="search_button" onClick={handleSearch}><FiSearch size={24}/></button>
                </div>
            </div>
            <div className="results">
                <ul className="suggestions">
                {filteredHomes.length > 0 ? (
                        filteredHomes.map((home, index) => (
                            <li key={index}>
                                <span className="name">{home.name}, {home.address_line_1}, {home.suburb}, {home.state}, {home.post_code}</span>

                            </li>
                        ))
                ) : (
                    <>
                        <li className="result">Filter for Provider, City, Suburb, State</li>
                    </>
                )}
                </ul>
            </div>
        </div>
    );
}

export default SearchBar;
